(()=>{const a=localStorage.getItem("display_antrian"),d=window.location.origin;window.logout=()=>{localStorage.removeItem("display_antrian"),setTimeout(()=>{window.location.reload()},300)};if(a){const o=document.getElementById("my_form"),t=document.getElementById("call");o.style.display="block";const n=document.getElementById("url_display"),l=document.getElementById("display_name");n.innerHTML=d+"/display",n.href=d+"/display",l.innerHTML=a,n.target="_blank";const i=document.getElementById("no"),m=document.getElementById("counter"),s=document.getElementById("form");s.onsubmit=function(c){c.preventDefault(),t.disabled!==!0&&fetch("./send/"+a,{method:"POST",body:JSON.stringify({no:i.value,counter:m.value})}).then(e=>{if(!e.ok)throw e;return e.json()}).then(e=>{t.disabled=!0,setTimeout(()=>{t.disabled=!1},7e3)}).catch(e=>{e.json().then(r=>{alert(r.message)})})}}else{const o=document.getElementById("my_create_form");o.style.display="block";const t=document.getElementById("display_form"),n=document.getElementById("display");t.onsubmit=function(l){l.preventDefault(),localStorage.setItem("display_antrian",n.value),setTimeout(()=>{window.location.reload()},500)}}})();
